import SuperPage from "./D_Painter_1.js"
import styled from "styled-components";
import theme from "../theme";
import Grid from '@material-ui/core/Grid';
import backSvg from "../../resource/BTN-18.svg"
import BTN1 from '../../resource/BTN-12.svg';
import BTN2 from '../../resource/BTN-13.svg';
import BTN3 from '../../resource/BTN-14.svg';
import Section from "../section02"
// import axios from "axios"

const ContentStyle = styled.div`
    width:1550px;
    height:100%;
    &>div>div{
        text-align:center;
    }
    &>div>div:nth-child(1){
        text-align:center;
        padding-top:30px;
        &>div{
            width: 845px;
            height: 39px;
            background: rgb(255,210,181) 0% 0% no-repeat padding-box;
            display:inline-block;
            margin-bottom:-67px;
        }
        &>h1{
            font: normal normal normal 47px/42px ${theme.GmarketFontMedium};
            letter-spacing: 0px;
            color: #3B3B3B;
            opacity: 1;
            margin-top:0;
        } 
    }
    &>div>div:nth-child(2){
        position:relative;
        & > img:nth-child(1){
            width: 375px;
            height: 470px;
            margin-top:115px;
        }
        & > img:nth-child(2){
            width: 100px;
            height: 100px;
            position:absolute;
            top:505px;
            left:50px;
            object-fit: cover;
        }
    }
    &>div>div:nth-child(3){
        position:relative;
        & > img:nth-child(1){
            width: 375px;
            height: 470px;
            margin-top:115px;
        }
        & > img:nth-child(2){
            width: 100px;
            height: 100px;
            position:absolute;
            top:505px;
            left:50px;
            object-fit: cover;
        }
    }
    &>div>div:nth-child(4){
        position:relative;
        & > img:nth-child(1){
            width: 375px;
            height: 470px;
            margin-top:115px;
        }
        & > img:nth-child(2){
            width: 100px;
            height: 100px;
            position:absolute;
            top:505px;
            left:50px;
            object-fit: cover;
        }
    }
    
    &>div>div:nth-child(5){
        img{
            ${theme.BubbleButton}
            margin-top:53px;
        }
    }
    &>div>div:nth-child(6){
        img{
            ${theme.BubbleButton}
            margin-top:53px;
        }
    }
    &>div>div:nth-child(7){
        img{
            ${theme.BubbleButton}
            margin-top:53px;
        }
    }
    &>div>div:nth-child(8){
        img{
            width: 74px;
            height: 74px;
            margin-top:60px;
        }
    }
    p{
        font: normal normal normal 23px/42px ${theme.GmarketFontMedium};
        letter-spacing: 0px;
        color: #3B3B3B;
        opacity: 1;
        margin:53px 0px 0px 0px;
    }
`;
class Page extends SuperPage {
    constructor(props) {
        super(props);
        this.state = {
            resultImg1: this.props.masterpieceResultImg[theme.PainterArts[this.props.painter][0]],
            resultImg2: this.props.masterpieceResultImg[theme.PainterArts[this.props.painter][1]],
            resultImg3: this.props.masterpieceResultImg[theme.PainterArts[this.props.painter][2]],
            masterpieceImg1 : theme.Masterpieces[theme.PainterArts[this.props.painter][0]],
            masterpieceImg2 : theme.Masterpieces[theme.PainterArts[this.props.painter][1]],
            masterpieceImg3 : theme.Masterpieces[theme.PainterArts[this.props.painter][2]]
        }
    }

    componentDidMount() {
    }

    content() {
        return (
            <ContentStyle>
                <Grid container>
                    <Grid item xs={12}>
                        <div></div>
                        <h1>[{this.props.painter}] 선택하였습니다</h1>
                    </Grid>
                    <Grid item xs={4}>
                        <img src={this.state.resultImg1} alt="#"></img>
                        <img src={this.state.masterpieceImg1} alt="#"></img>
                    </Grid>
                    <Grid item xs={4}>
                        <img src={this.state.resultImg2} alt="#"></img>
                        <img src={this.state.masterpieceImg2} alt="#"></img>
                    </Grid>
                    <Grid item xs={4}>
                        <img src={this.state.resultImg3} alt="#"></img>
                        <img src={this.state.masterpieceImg3} alt="#"></img>
                    </Grid>

                    <Grid item xs={4}>
                        <p>{theme.PainterArts[this.props.painter][0]}</p>
                        <img draggable="false" src={BTN1} alt="#" onClick={() => { this.props.setMasterpiece(theme.PainterArts[this.props.painter][0]); this.props.setPageNum("7") }} />
                    </Grid>
                    <Grid item xs={4}>
                        <p>{theme.PainterArts[this.props.painter][1]}</p>
                        <img draggable="false" src={BTN2} alt="#" onClick={() => { this.props.setMasterpiece(theme.PainterArts[this.props.painter][1]); this.props.setPageNum("7") }} />
                    </Grid>
                    <Grid item xs={4}>
                        <p>{theme.PainterArts[this.props.painter][2]}</p>
                        <img draggable="false" src={BTN3} alt="#" onClick={() => { this.props.setMasterpiece(theme.PainterArts[this.props.painter][2]); this.props.setPageNum("7") }} />
                    </Grid>
                </Grid>
            </ContentStyle>
        )
    }

    backButton() {
        let backpage = (parseInt(theme.Painter.indexOf(this.props.painter)/3)+4).toString()
        return (
            <img draggable="false" src={backSvg} alt="#" onClick={() => this.props.setPageNum(backpage)} />
        )
    }

    render() {
        return (
            <Section id="result">
                {this.commonSection()}
            </Section>
        )
    }
}

export default Page;
