import React, { Component } from 'react';
import styled from "styled-components";
import theme from "../theme"
import Grid from '@material-ui/core/Grid';
import BTN from '../../resource/BTN-01.svg'
import Image1 from '../../resource/main1.jpg';
import Image2 from '../../resource/main2.jpg';

const Section = styled.section`
    background-color:${theme.OrangeColor};

    &>div>div:nth-child(2){
        height:612px;
        text-align:center;
    }
    &>div>div:nth-child(3){
        height:612px;
        text-align:center;
    }
    &>div>div:nth-child(5){
        height:276px;
        text-align:center;
    }
    img{
        width: auto;
        height: 510px;
        margin-top:102px;
    }
    h2{
        letter-spacing: 0px;
        color: #FFFFFF;
        opacity: 1;
        font: normal normal bold 54px/65px ${theme.GmarketFontBold};
        // margin:90px 0 47px 0;
        margin:46px 0 38px 0;
    }
    p{
        letter-spacing: 0px;
        color: ${theme.LightWhiteColor};
        opacity: 1;
        font: normal normal 31px/40px ${theme.GmarketFontLight};
        text-align: center;
        margin:0;
    }
    &>div>div:nth-child(6){
        height:192px;
        text-align:center;
        & > img{
            ${theme.BubbleButton}
            margin:0;
        }
    }
`;

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
        }
    }
    render() {
        return (
            <Section id="home">
                <Grid container
                    direction="row"
                    justify="center"
                    alignItems="center">
                    <Grid item xs={1}></Grid>
                    <Grid item xs={5}>
                        <img src={Image1} alt="#"></img>
                    </Grid>
                    <Grid item xs={5}>
                        <img src={Image2} alt="#"></img>
                    </Grid>
                    <Grid item xs={1}></Grid>
                    <Grid item xs={12}>
                        <h2>화가의 채색을 입혀드립니다</h2>
                        <p>AI를 이용하여 작품에 대한 전반적인<br />질감과 색을 학습해 입혀드립니다</p>
                    </Grid>
                    <Grid item xs={12}>
                        <img src={BTN} alt="#" onClick={() => this.props.setPageNum("2")} ></img>
                    </Grid>
                </Grid>
            </Section>
        )
    }
}

export default Page;
